$.nette.ext("ublaboo-spinners",{before:function(i,e){var n,a;if(e.nette){if(n=e.nette.el,a=$('<div class="ublaboo-spinner ublaboo-spinner-small"><i></i><i></i><i></i><i></i></div>'),n.is(".datagrid #group_action_submit"))return n.after(a);if(n.is(".datagrid a")&&n.data("toggle-detail"))return n.addClass("ublaboo-spinner-icon");if(n.is(".datagrid .pagination a"))return n.closest(".row-grid-bottom").find("select[name=per_page]").before(a);if(n.is(".datagrid .dropdown-menu a"))return n.addClass("ublaboo-spinner-icon")}},success:function(){return $(".ublaboo-spinner").remove(),$(".ublaboo-spinner-icon").removeClass("ublaboo-spinner-icon")}});